import React from "react";
import WithSpinner from "../components/WithSpinner";
import CollectionsOverviewContainer from "../components/CollectionOverview";

import { connect } from "react-redux";
import { fetchCollectionsStartAsync } from "../redux/ShopAction";
import { Route } from "react-router-dom";
import CollectionContainer from "./CollectionContainer";

const CollectionOverviewSpinner = WithSpinner(CollectionsOverviewContainer);
const CollectionSpinner = WithSpinner(CollectionContainer);
class Shop extends React.Component {
  componentDidMount() {
    const { fetchCollectionsStartAsync } = this.props;
    fetchCollectionsStartAsync();
  }

  render() {
    const { match } = this.props;

    return (
      <div className="shop-page">
        <Route
          exact
          path={`${match.path}`}
          component={<CollectionOverviewSpinner />}
        />
        <Route
          path={`${match.path}/:collectionId`}
          componentDidMount={<CollectionSpinner />}
        />
      </div>
    );
  }
}

const mapDispatchToProps = (dispatch) => ({
  fetchCollectionsStartAsync: () => dispatch(fetchCollectionsStartAsync()),
});
export default connect(null, mapDispatchToProps)(Shop);
