import React from "react";
import WithSpinner from "../components/WithSpinner";
import CollectionOverview from "../components/CollectionOverview";
import { createStructuredSelector } from "reselect";
import { connect } from "react-redux";
import { fetchCollectionsStartAsync } from "../redux/ShopAction";
import { Route } from "react-router-dom";
import Collection from "./Collection";
import {
  selectIsCollectionFetching,
  selectIsCollectionsLoaded,
} from "../redux/ShopSelectors";

const CollectionOverviewSpinner = WithSpinner(CollectionOverview);
const CollectionSpinner = WithSpinner(Collection);
class Shop extends React.Component {
  componentDidMount() {
    const { fetchCollectionsStartAsync } = this.props;
    fetchCollectionsStartAsync();
  }

  render() {
    const { match } = this.props;
    const { Fetching, Loaded } = this.props;

    return (
      <div className="shop-page">
        <Route
          exact
          path={`${match.path}`}
          render={(props) => (
            <CollectionOverviewSpinner isLoading={!Loaded} {...props} />
          )}
        />
        <Route
          path={`${match.path}/:collectionId`}
          render={(props) => (
            <CollectionSpinner isLoading={!Loaded} {...props} />
          )}
        />
      </div>
    );
  }
}
const mapStateToProps = createStructuredSelector({
  Fetching: selectIsCollectionFetching,
  Loaded: selectIsCollectionsLoaded,
});

const mapDispatchToProps = (dispatch) => ({
  fetchCollectionsStartAsync: () => dispatch(fetchCollectionsStartAsync()),
});
export default connect(mapStateToProps, mapDispatchToProps)(Shop);
