import { ShopActionTypes } from "./ShopTypes";
const INITAL_STATE = {
  collections: null,
  isFetching: true,
};

const ShopReducer = (state = INITAL_STATE, action) => {
  switch (action.type) {
    case ShopActionTypes.FET:
      return { ...state, collections: action.payload };
    default:
      return state;
  }
};
export default ShopReducer;
