const INITIAL_STATE = {
  curentUser: null,
};

const UserReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case "SET_CURRENT_USER":
      return { ...state, curentUser: action.payload };
    default:
      return state;
  }
};
export default UserReducer;
